<div class="post clearfix">	<h2 class="title"><a href="<?php the_permalink(); ?>"><?php the_title(); ?></a></h2>	<?php include(TEMPLATEPATH . '/includes/postinfo.php'); ?>	<?php 		$thumb = '';		$width = 188;		$height = 188;		$classtext = 'post-thumb';		$titletext = get_the_title();				$thumbnail = get_thumbnail($width,$height,$classtext,$titletext,$titletext,false,'Entry');		$thumb = $thumbnail["thumb"]; 	?>		<?php if($thumb <> '' && get_option('modest_thumbnails_index') == 'on') { ?>		<div class="post-thumbnail">			<a href="<?php the_permalink(); ?>">				<?php print_thumbnail($thumb, $thumbnail["use_timthumb"], $titletext, $width, $height, $classtext); ?>				<span class="post-overlay"></span>			</a>			</div> 	<!-- end .post-thumbnail -->	<?php } ?>	<?php if ( get_option('modest_blog_style') == 'false' ){ ?>		<p><?php truncate_post(680); ?></p>	<?php } else { ?>		<?php the_content(''); ?>	<?php } ?>	<a href="<?php the_permalink(); ?>" class="readmore"><span><?php _e('Read More','Modest'); ?></span></a></div> 	<!-- end .post-->